import React, { useState } from "react";
import styled from "styled-components";

const SearchContainer = styled.div`
  height: 100vh;
  width: 100%;
  background: gray;
`;

const SearchInput = styled.input`
  box-shadow: none;
  height: 48px;
  border-radius: 100px;
  border: none;
  font-size: 16px;
  color: #6f737a;
  width: 50%;
`;

const SearchButton = styled.button`
  text-align: center;
  border: none;
  box-shadow: none;
  font-size: 16px;
  font-weight: 700;
  color: #fff;
  border-radius: 100px;
  background: #3177bb;

  height: 50px;
  margin-left: -50px;

  text-align: center;
  width: 200px;
`;

const Search = ({ getRecipeBySearch }) => {
  const [search, setSearch] = useState("");

  const onChange = (e) => {
    setSearch(e.target.value);
  };

  const onSubmit = (e) => {
    e.preventDefault();
    getRecipeBySearch(search);
  };

  return (
    <SearchContainer>
      <SearchInput onChange={onChange} placeholder="Search recipes..." />
      <SearchButton onClick={onSubmit}>Search</SearchButton>
    </SearchContainer>
  );
};

export default Search;
